import React, {useEffect, useState} from 'react';
import {Link} from "react-router-dom";
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import TodoList from '../components/TodoList';
import MyModal from '../components/MyModal';
import axios from 'axios';

const Home = () => {
  const [todoTxt, setTodo] = useState("");
  const [show, setShow] = useState(false);
  const [showDeleteAlert, setShowDelete] = useState(false);
  const [todoList, setList] = useState([]);
  const [updateHook, setUpdateHook] = useState(false);
  useEffect(() => {
    axios.post('/api/todo', {})
      .then(res => {setList(res.data)})
      .catch(e => {console.log(e)});
  }, [updateHook]);

  const changeTxt = e => setTodo(e.target.value);
  const addTodo = () => {
    if(todoTxt.length < 2){
      setShow(true);
      return
    }
    axios.post('/api/add', {param:[todoTxt]})
      .then(() => setUpdateHook(prev => !prev))
      .catch(e => {console.log(e)});
    setTodo("");
  }
  const onKeyPress = e => {if(e.key === 'Enter') addTodo();}

  const deleteTodo = id => {
    axios.post('/api/delete', {param:[id]})
      .then(() => {
        setUpdateHook(prev => !prev);
        setShowDelete(true);
        })
      .catch(e => {console.log(e)});
  }
  const handleClose = () => setShow(false);
  const TodoHtml = ({todoData}) => {
    return (
      <Row className="justify-content-center mt-4">
        <Col lg={8} md={10} xs={12}>
          {<TodoList 
            title={todoData.title} 
            alert={todoData.alert} 
            beforealert={todoData.alertbefore} 
            datetime={todoData.datetime} 
            deletetodo={deleteTodo} 
            id={todoData.id}/>}
        </Col>
      </Row>
    );
  }
  return (
    <main>
      <Container>
        <Row className="justify-content-center mt-4">
          <Col lg={8} md={10} xs={12}><Link to="/" style={{textDecoration: "none", color: "black"}}><h1 className="text-center">My Todo List</h1></Link></Col>
        </Row>
        <Row className="justify-content-center mt-3">
          <Col lg={8} md={10} xs={12}>
            <InputGroup size="lg">
              <Form.Control
                placeholder="What's to do...?"
                aria-label="Large"
                aria-describedby="inputGroup-sizing-sm"
                value={todoTxt}
                onChange={changeTxt}
                onKeyPress={onKeyPress}
              />
              <InputGroup.Text id="inputGroup-sizing-lg" onClick={addTodo} style={{cursor: "pointer"}}><span className="material-icons">add</span></InputGroup.Text>
            </InputGroup>
          </Col>
        </Row>
      </Container>
      {/* modal */}
      <MyModal title="알림" body="최소 2글자 이상 입력해주세요!" Show={show} handleClose={handleClose} />
      <MyModal title="알림" body="삭제되었습니다." Show={showDeleteAlert} handleClose={setShowDelete} />
      <Container>
        {todoList.map(todo => <TodoHtml todoData={todo} key={todo.id}/>)}
      </Container>
    </main>
  );
}

export default Home;
