import React from "react";
import axios from 'axios';
import { REST_API_KEY, REDIRECT_URI } from "../kakaoLoginData";
import {useNavigate} from "react-router-dom";

const KakaoRedirectHandler = () => {
  const navigate = useNavigate();
  const login = async (kakao_account) => {
    await axios.post("/api/login", {
      param:[
        {email: kakao_account.email, nickname: kakao_account.profile.nickname},
        {nickname: kakao_account.profile.nickname}
      ]
    });
  }
  React.useEffect(()=> {
    let params = new URL(window.location.href).searchParams.get("code");
    let grant_type = "authorization_code";
    
    const getProfile = async () => {
      try {
        let data = await window.Kakao.API.request({url: "/v2/user/me"});
        login(data.kakao_account);
        localStorage.setItem("user_email", data.kakao_account.email);
      } catch (err) {
        console.log(err);
      }
    };

    axios.post(`https://kauth.kakao.com/oauth/token?grant_type=${grant_type}&client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&code=${params}`
      , {
      headers: {
          'Content-type': 'application/x-www-form-urlencoded;charset=utf-8'
      }
    }).then((res) => {
      window.Kakao.init(REST_API_KEY);
      window.Kakao.Auth.setAccessToken(res.data.access_token);
      getProfile();
      navigate("/");
    })

    }, [navigate])
  return null;
};

export default KakaoRedirectHandler;
