import React, {useState} from 'react';
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

const Visible = ({handleClose, handleCancle}) => {
  return (
    <Button variant="primary" onClick={() => {handleClose(false); handleCancle(true)}}>
      확인
    </Button>
  )
}

const MyModal = ({title, body, Show, handleClose, saveChange=false, handleCancle}) => {
  const [isVisible, setVisible] = useState({visibility: "hidden"});
  if(saveChange) setVisible({visibility: "visible"});
  return (
    <Modal show={Show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>{title}</Modal.Title>
      </Modal.Header>
      <Modal.Body>{body}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={() => {handleClose(false); handleCancle(false)}}>
          닫기
        </Button>
        <Button variant="primary" onClick={() => {handleClose(false);}}>
          확인
        </Button>
        {/* {saveChange && <Visible handleClose={handleClose} handleCancle={handleCancle}/>} */}
      </Modal.Footer>
    </Modal>
  )
}

export default MyModal;
