import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import BasicDateTimePicker from './DateTimePicker';
import Form from 'react-bootstrap/Form';

const TodoList = (title, date, time, alert=false) =>{
  const [onOff, oaoChange] = useState(false);
  const offChange = e => oaoChange(e.target.checked);
  return(
    <Accordion defaultActiveKey="0">
      <Accordion.Item eventKey="0">
        <Accordion.Header>{title}</Accordion.Header>
        <Accordion.Body>
          <Container>
            <Row>
              <Col lg={7} md={8} sm={12} className="pt-3">
                <BasicDateTimePicker  className="align-middle" />
              </Col>
              <Col lg={5} md={4} sm={12} className="position-relative">
                <p className='pt-2 mb-1' style={{color: "rgba(0, 0, 0, 0.6)", fontSize: "13px"}}>알림여부</p>
                <Form>
                  <Form.Check 
                    type="switch"
                    id="custom-switch"
                    label={onOff ? "On" : "Off"}
                    checked={onOff}
                    onChange={offChange}
                  />
                </Form>
                <Button className="position-absolute end-0" variant="primary">확인</Button>{' '}
              </Col>
            </Row>
          </Container>
          
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  )
}

export default TodoList;
