import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import BasicDateTimePicker from './DateTimePicker';

const TodoList = (title, date, time, alert=false) =>{
  const [radioValue, setRadioValue] = useState('2');
  if(alert) setRadioValue('1');
  const radios = [
    { name: 'On', value: '1' },
    { name: 'Off', value: '2' }
  ];
  return(
    <Accordion defaultActiveKey="0">
      <Accordion.Item eventKey="0">
        <Accordion.Header>{title}</Accordion.Header>
        <Accordion.Body>
          <Container>
            <Row>
              <Col lg={7} md={8} sm={12} className="pt-3">
                <BasicDateTimePicker  className="align-middle" />
              </Col>
              <Col lg={5} md={4} sm={12} className="position-relative">
                <p className='pt-2 mb-1' style={{color: "rgba(0, 0, 0, 0.6)"}}>알림여부</p>
                <ButtonGroup>
                  {radios.map((radio, idx) => (
                    <ToggleButton
                      key={idx}
                      id={`radio-${idx}`}
                      type="radio"
                      variant={idx % 2 ? 'outline-danger' : 'outline-success'}
                      name="radio"
                      value={radio.value}
                      checked={radioValue === radio.value}
                      onChange={(e) => setRadioValue(e.currentTarget.value)}
                    >
                      {radio.name}
                    </ToggleButton>
                  ))}
                </ButtonGroup>
                <Button className="position-absolute end-0" variant="primary">확인</Button>{' '}
              </Col>
            </Row>
          </Container>
          
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  )
}

export default TodoList;
