import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import BasicDateTimePicker from './DateTimePicker';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

const TodoList = (title, datetime, alert=false, alerttime=0) =>{
  const [onOff, oaoChange] = useState(alert);
  const [dateTime, setDateTime] = useState(new Date());
  const [alertTime, setAlert] = useState(alerttime);
  if(datetime != null) setDateTime(datetime); 
  const changeDate = date => setDateTime(date);
  const offChange = e => oaoChange(e.target.checked);
  const changeAlertTime = e => setAlert(e.target.value);
  return(
    <Accordion defaultActiveKey="0">
      <Accordion.Item eventKey="0">
        <Accordion.Header>{title}</Accordion.Header>
        <Accordion.Body>
          <Container>
            <Row>
              <Col lg={9} md={8} sm={12} className="pt-3">
                <BasicDateTimePicker dateTimeValue={dateTime} setValue={changeDate}/>
              </Col>
              <Col lg={3} md={4} sm={12} className="position-relative">
                <p className='py-2 mb-1' style={{color: "rgba(0, 0, 0, 0.6)", fontSize: "13px"}}>알림여부</p>
                <Form>
                  <Form.Check 
                    type="switch"
                    id="custom-switch"
                    label={onOff ? "On" : "Off"}
                    checked={onOff}
                    onChange={offChange}
                  />
                </Form>
              </Col>
            </Row>
            <Row>
              <Col lg={10} md={9} sm={12} className="mt-4">
                <InputGroup className="mb-3">
                  <Form.Control
                    placeholder="알림 시간"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    type='number'
                    value={alertTime}
                    onChange={changeAlertTime}
                  />
                  <InputGroup.Text id="basic-addon2">분 후에 알림</InputGroup.Text>
                </InputGroup>
              </Col>
              <Col lg={2} md={3} sm={4} className="mt-4">
                <Button variant="primary">적용</Button>{' '}
              </Col>
            </Row>
          </Container>
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  )
}

export default TodoList;
