import React, {useState} from 'react';
import {Link} from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import TodoList from '../components/TodoList';

const Home = () => {
  const [todoTxt, setTodo] = useState("");
  const [show, setShow] = useState(false);
  //const [todoList, setList] = useState([]);
  const changeTxt = (e) => {
    setTodo(e.target.value);
  }
  const addTodo = () => {
    if(todoTxt.length < 2){
      setShow(true);
      return
    }
    setTodo("");
  }
  const handleClose = () => setShow(false);
  return (
    <main>
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand href="/">Todo</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/">Home</Nav.Link>
            <Nav.Link href="#features">Features</Nav.Link>
            <Nav.Link href="#pricing">Pricing</Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>
        </Container>
      </Navbar>
      <Container>
        <Row className="justify-content-center mt-4">
          <Col lg={8} md={8} xs={12}><Link to="/" style={{textDecoration: "none", color: "black"}}><h1 class="text-center">My Todo List</h1></Link></Col>
        </Row>
        <Row className="justify-content-center mt-3">
          <Col lg={8} md={8} xs={12}>
            <InputGroup size="lg">
              <Form.Control
                placeholder="What's to do...?"
                aria-label="Large"
                aria-describedby="inputGroup-sizing-sm"
                value={todoTxt}
                onChange={changeTxt}
              />
              <InputGroup.Text id="inputGroup-sizing-lg" onClick={addTodo} style={{cursor: "pointer"}}><span class="material-icons">add</span></InputGroup.Text>
            </InputGroup>
          </Col>
        </Row>
      </Container>
      {/* modal */}
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>알림</Modal.Title>
        </Modal.Header>
        <Modal.Body>최소 2글자 이상 입력해주세요!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      <Container>
        <Row className="justify-content-center mt-4">
          <Col lg={8} md={10} xs={12}>
            {TodoList("test")}
          </Col>
        </Row>
      </Container>
    </main>
  )
}

export default Home;
