import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import ButtonGroup from 'react-bootstrap/ButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import DatePicker from './DatePicker';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import TimePicker from 'react-time-picker';
import DateTimePicker from './DateTimePicker';

const TodoList = (title, date, time, alert=false) =>{
  const [radioValue, setRadioValue] = useState('2');
  const [value, onChange] = useState();
  if(alert) setRadioValue('1');
  const radios = [
    { name: 'On', value: '1' },
    { name: 'Off', value: '2' }
  ];
  const [aDate, setValue] = React.useState(new Date('2014-08-18T21:11:54'));
 
  const handleChange = (newValue) => {
    setValue(newValue);
  };
  return(
    <Accordion defaultActiveKey="0">
      <Accordion.Item eventKey="0">
        <Accordion.Header>{title}</Accordion.Header>
        <Accordion.Body>
          <Container>
            <Row>
              <Col>
                <p className='pt-2'>날짜</p>
                {DatePicker()}
              </Col>
              <Col>
                <p className='pt-2'>시간</p>
                <TimePicker onChange={onChange} value={value} />
              </Col> 
            </Row>
            <Row>
              <Col lg={5} md={4} sm={12} className="position-relative">
                <p className='pt-2'>알림여부</p>
                <ButtonGroup>
                  {radios.map((radio, idx) => (
                    <ToggleButton
                      key={idx}
                      id={`radio-${idx}`}
                      type="radio"
                      variant={idx % 2 ? 'outline-danger' : 'outline-success'}
                      name="radio"
                      value={radio.value}
                      checked={radioValue === radio.value}
                      onChange={(e) => setRadioValue(e.currentTarget.value)}
                    >
                      {radio.name}
                    </ToggleButton>
                  ))}
                </ButtonGroup>
                <DateTimePicker
                  label="날짜&시간"
                  value={aDate}
                  onChange={handleChange}
                  renderInput={(params) => <TextField {...params} />}
                />
                <Button className="position-absolute end-0" variant="primary">확인</Button>{' '}
              </Col>
            </Row>
          </Container>
          
        </Accordion.Body>
      </Accordion.Item>
    </Accordion>
  )
}

export default TodoList;
