import React, {useState} from 'react';
import {Link} from "react-router-dom";

const Home = () => {
  const [todoTxt, setText] = useState("");
  const changeTxt = (e) => {
    setText(e.target.value);
  }
  const addTodo = () => {
    if(todoTxt === ""){
      return (
        <div class="modal" tabindex="-1">
          <div class="modal-dialog">
            <div class="modal-content">
              <div class="modal-header">
                <h5 class="modal-title">알림</h5>
                <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              </div>
              <div class="modal-body">
                <p>최소 2자 이상 입력해주세요!</p>
              </div>
              <div class="modal-footer">
                <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              </div>
            </div>
          </div>
        </div>
      )
    }
    setText(todoTxt.length);
  }
  return (
    <main>
      <nav class="navbar navbar-light" style={{backgroundColor: "#e3f2fd"}}>
        <div class="container-fluid">
          <h1></h1>
          <form class="d-flex">
            <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
            <button class="btn btn-outline-success" type="submit">Search</button>
          </form>
        </div>
      </nav>
      <div class="container">
        <div class="row justify-content-center mt-4">
          <div class="col-8"><Link to="/" style={{textDecoration: "none", color: "black"}}><h1 class="text-center">My Todo List</h1></Link></div>
        </div>
        <div class="row justify-content-center mt-3">
          <div class="col-8">
            <div class="input-group input-group-lg">
              <input type="text" value={todoTxt} class="form-control" onChange={changeTxt} placeholder="What's to do...?" aria-describedby="inputGroup-sizing-lg"/>
              <span class="input-group-text" id="inputGroup-sizing-lg" onClick={addTodo} style={{cursor: "pointer"}}><span class="material-icons">add</span></span>
            </div>
          </div>
        </div>
        <p>{todoTxt}</p>
      </div>
    </main>
  )
}

export default Home;
