import React, {useEffect, useState} from 'react';
import {Link} from "react-router-dom";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import InputGroup from 'react-bootstrap/InputGroup';
import TodoList from '../components/TodoList';
import MyModal from '../components/MyModal';
import axios from 'axios';

const Home = () => {
  const [todoTxt, setTodo] = useState("");
  const [show, setShow] = useState(false);
  const [tempAlert, setTemp] = useState(new Date());
  const [showAlert, setShowAlert] = useState(false);
  const [showNon, setShowNon] = useState(false);
  const [showDeleteAlert, setShowDelete] = useState(false);
  const [todoList, setList] = useState([]);
  useEffect(() => {
    axios.post('/api/todo', {})
      .then(res => {setList(res.data)})
      .catch(e => {console.log(e)});
  }, [])
  const updateTodo = (fromChild=false, id=0) => {
    axios.post('/api/todo', {})
      .then(res => {
        setList(() => res.data);
        if(fromChild) {
          setTemp(() => new Date(todoList.filter(e => e.id === id)[0].alerttime * 1000));
          if(todoList.filter(e => e.id === id)[0].alert) setShowAlert(true);
          else setShowNon(true);
        }
      })
      .catch(e => {console.log(e)});
  }

  console.log(todoList);
  const changeTxt = e => setTodo(e.target.value);
  const addTodo = () => {
    if(todoTxt.length < 2){
      setShow(true);
      return
    }
    axios.post('/api/add', {param:[todoTxt]})
      .then(() => updateTodo())
      .catch(e => {console.log(e)});
    setTodo("");
  }
  const onKeyPress = e => {if(e.key === 'Enter') addTodo();}

  const deleteTodo = id => {
    axios.post('/api/delete', {param:[id]})
      .then(() => {
        updateTodo();
        setShowDelete(true);
        })
      .catch(e => {console.log(e)});
  }
  const handleClose = () => setShow(false);
  const TodoHtml = ({todoData}) => {
    
    return (
      <Row className="justify-content-center mt-4">
        <Col lg={8} md={10} xs={12}>
          {<TodoList 
            title={todoData.title} 
            alert={todoData.alert} 
            beforealert={todoData.alertbefore} 
            datetime={todoData.datetime} 
            deletetodo={deleteTodo} 
            id={todoData.id} 
            updateTodo={updateTodo} />}
        </Col>
      </Row>
    );
  }
  return (
    <main>
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand href="/">Todo</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="/">Home</Nav.Link>
            <Nav.Link href="#features">Features</Nav.Link>
            <Nav.Link href="#pricing">Pricing</Nav.Link>
          </Nav>
          <Form className="d-flex">
            <Form.Control
              type="search"
              placeholder="Search"
              className="me-2"
              aria-label="Search"
            />
            <Button variant="outline-success">Search</Button>
          </Form>
        </Container>
      </Navbar>
      <Container>
        <Row className="justify-content-center mt-4">
          <Col lg={8} md={10} xs={12}><Link to="/" style={{textDecoration: "none", color: "black"}}><h1 class="text-center">My Todo List</h1></Link></Col>
        </Row>
        <Row className="justify-content-center mt-3">
          <Col lg={8} md={10} xs={12}>
            <InputGroup size="lg">
              <Form.Control
                placeholder="What's to do...?"
                aria-label="Large"
                aria-describedby="inputGroup-sizing-sm"
                value={todoTxt}
                onChange={changeTxt}
                onKeyPress={onKeyPress}
              />
              <InputGroup.Text id="inputGroup-sizing-lg" onClick={addTodo} style={{cursor: "pointer"}}><span class="material-icons">add</span></InputGroup.Text>
            </InputGroup>
          </Col>
        </Row>
      </Container>
      {/* modal */}
      <MyModal title="알림" body="최소 2글자 이상 입력해주세요!" Show={show} handleClose={handleClose} />
      <MyModal title="알림" body="삭제되었습니다." Show={showDeleteAlert} handleClose={setShowDelete} />
      <MyModal title="알림" body={`${tempAlert.toString()}에 알림이 울립니다.`} Show={showAlert} handleClose={setShowAlert} />
      <MyModal title="알림" body="저장되었습니다." Show={showNon} handleClose={setShowNon}/>
      <Container>
        {todoList.map(todo => <TodoHtml todoData={todo} key={todo.id}/>)}
      </Container>
    </main>
  );
}

export default Home;
