import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import BasicDateTimePicker from './DateTimePicker';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import MyModal from './MyModal';


const TodoList = ({title, datetime, alert=false, alertbeforetime=0}) =>{
  const [onOff, oaoChange] = useState(alert);
  const [dateTime, setDateTime] = useState(datetime);
  const [alertBeforeTime, setBeforeAlert] = useState(alertbeforetime);
  const [alertTime, setAlertTime] = useState(new Date());
  const [showAlert, setShowAlert] = useState(false);
  const [showNon, setShowNon] = useState(false);
  console.log(dateTime); 
  const changeDate = date => setDateTime(date);
  const offChange = e => oaoChange(e.target.checked);
  const changeAlertTime = e => setBeforeAlert(e.target.value);
  const apply = () => {
    setAlertTime(new Date(dateTime.getTime() - (alertBeforeTime*60*1000)));
    if(onOff) setShowAlert(true);
    else setShowNon(true);
  }
  return(
    <main>
      <Accordion alwaysOpen>
        <Accordion.Item eventKey="0">
          <Accordion.Header>{title}</Accordion.Header>
          <Accordion.Body>
            <Container>
              <Row>
                <Col lg={9} md={8} sm={12} className="pt-3">
                  <BasicDateTimePicker dateTimeValue={dateTime} setValue={changeDate}/>
                </Col>
                <Col lg={3} md={4} sm={12} className="text-center">
                  <p className='py-2 mb-1' style={{color: "rgba(0, 0, 0, 0.6)", fontSize: "13px"}}>알림여부</p>
                  <Form>
                    <Form.Check 
                      type="switch"
                      id="custom-switch"
                      label={onOff ? "On" : "Off"}
                      checked={onOff}
                      onChange={offChange}
                      className="align-items-center"
                    />
                  </Form>
                </Col>
              </Row>
              <Row>
                <Col xl={8} lg={7} md={7} sm={12} className="mt-4">
                  <InputGroup className="mb-3">
                    <Form.Control
                      placeholder="알림 시간"
                      aria-label="Recipient's username"
                      aria-describedby="basic-addon2"
                      type='number'
                      value={alertBeforeTime}
                      onChange={changeAlertTime}
                    />
                    <InputGroup.Text id="basic-addon2">분 전에 알림</InputGroup.Text>
                  </InputGroup>
                </Col>
                <Col xl={4} lg={5} md={5} sm={12} className="text-center mt-4">
                  <Button variant="primary" onClick={apply}>적용</Button>{' '}
                  <Button variant="secondary" onClick={apply}>취소</Button>{' '}
                  <Button variant="danger" onClick={apply}>삭제</Button>{' '}
                </Col>
              </Row>
            </Container>
          </Accordion.Body>
        </Accordion.Item>
      </Accordion>
      <MyModal title="알림" body={`${alertTime.toString()}에 알림이 울립니다.`} Show={showAlert} handleClose={setShowAlert} />
      <MyModal title="알림" body="저장되었습니다." Show={showNon} handleClose={setShowNon}/>
    </main>
  )
}

export default TodoList;
