import React from 'react';
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/esm/Container';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import { REST_API_KEY, REDIRECT_URI, CLIENT_ID, CLIENT_PASSWORD } from '../LoginData';
import { GoogleLogin } from 'react-google-login';
import {useNavigate} from "react-router-dom";

const Login = () => {

  const onSuccess = async (res) => {
    const profile = res.getBasicProfile();
    const userdata = {
      email: profile.getEmail(),
      image: profile.getImageUrl(),
      name: profile.getName(),
    }; 
    console.log(userdata);
  }

  const onFailure = (res) => {
    alert("구글 로그인에 실패하였습니다");
    console.log("err", res);
  };

  const KAKAO_AUTH_URL = `https://kauth.kakao.com/oauth/authorize?client_id=${REST_API_KEY}&redirect_uri=${REDIRECT_URI}&response_type=code`
  return (
    <main>
      <Container>
        <Row style={{height:"250px"}}></Row>
        <Row className='justify-content-center'>
          <Card style={{ width: '22rem' }}>
            <Card.Body>
              <Card.Title>
                <h3>로그인</h3> 
                <span className='py-2' style={{color: "rgba(0, 0, 0, 0.6)", fontSize: "13px"}}>아이디</span>
                <InputGroup className="mb-3 mt-2">
                  <Form.Control type='text'/>
                </InputGroup>
                <span className='py-2' style={{color: "rgba(0, 0, 0, 0.6)", fontSize: "13px"}}>비밀번호</span>
                <InputGroup className="mb-3 mt-2">
                  <Form.Control type='password' />
                </InputGroup>
                <Container>
                  <Row className='justify-content-center'>
                    <Col className="text-center">
                    <a href={KAKAO_AUTH_URL}><img src={require("../icons/kakao.png")} style={{height:"30px", width:"auto"}} alt="..." /></a>
                    </Col>
                    <Col className="text-center">
                      <GoogleLogin
                        clientId={CLIENT_ID}
                        render={renderProps => (
                          <img onClick={renderProps.onClick} 
                            src={require("../icons/google.png")} 
                            style={{height:"28px", width:"auto", cursor:"pointer"}} 
                            alt="..." />
                        )}
                        onSuccess={onSuccess}
                        onFailure={onFailure}
                        cookiePolicy={'single_host_origin'}
                        />
                    </Col>
                    <Col className="text-center">
                    <a href='/naver'><img src={require("../icons/naver.png")} style={{height:"30px", width:"auto"}} alt="..." /></a>
                    </Col>
                  </Row>
                </Container>
              </Card.Title>
              <Container>
                <Row className='border-top pt-3'>
                  <Col className="d-grid mb-2">
                    <Button variant="outline-primary">로그인</Button>  
                  </Col>
                  <Col className="d-grid mb-2">
                    <Button variant="outline-secondary">회원가입</Button>
                  </Col>
                </Row>
              </Container>
            </Card.Body>
          </Card>
        </Row>
      </Container>
    </main>
  )
}

export default Login;
